import { useState } from "react";

function App() {
  const [name, setName] = useState("");
  const [greeted, setGreeted] = useState(false);
  let [howmuch, setShow] = useState(0);

  const handleChange = (event) => {
    setName(event.target.value);
    setGreeted(false);
  };

  const handleGreet = () => {
    if (name.trim() !== "") {
      setGreeted(true);
      howmuch += 1;
    }
  };

  return (
    <div>
      <h1>Привет, {name || "гость"}!</h1>

      <input
        type="text"
        placeholder="Введите своё имя"
        value={name}
        onChange={handleChange}
      />

      <button onClick={handleGreet}>Поприветствовать</button>
      <p>Ты нажал кнопку {howmuch} раз! </p>

      {greeted && (
        <p style={{ color: "green", fontWeight: "bold"}}>
          Рад тебя видеть, {name}!
        </p>
      )}
    </div>
  );
}

export default App;
