  };

  return (
    <div className="App-header">
      <center>
      <h1>Привет, {name}!</h1>
      <p>Это твой первый {reactive}-компонент.</p>
      <p>Сегодня ты учишь {reactive} {"<-"} переменная.</p>
      </center>
    </div>
  );
}

export default App;
