import { useState } from "react";

function App() {
  const [path, setPath] = useState("");
  const [messages, setMessages] = useState([]);

  const fetchMessage = () => {
    fetch(`http://localhost:8080${path}`)
      .then(resp => {
        if (!resp.ok) {
          throw new Error(`–û—à–∏–±–∫–∞ —Å–µ—Ä–≤–µ—Ä–∞: ${resp.status}`);
        }
        return resp.json();
      })
      .then(data => {
        if (data.message) {
          setMessages(prev => [...prev, data.message]);
        } else {
          console.warn('–ù–µ—Ç –ø–æ–ª—è message –≤ –æ—Ç–≤–µ—Ç–µ:', data);
        }
      })
      .catch(error => {
        console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏ –ø–æ–ª—É—á–µ–Ω–∏–∏ –¥–∞–Ω–Ω—ã—Ö:', error);
      })
  };

  return (
    <div>
      <h1>–°–ø–∏—Å–æ–∫ –ø—Ä–∏–≤–µ—Ç—Å—Ç–≤–∏–π üëã</h1>

      <input
        type="text"
        placeholder="–í–≤–µ–¥–∏—Ç–µ –∏–º—è"
        value={path}
        onChange={(e) => setPath(e.target.value)}
      />

      <button onClick={fetchMessage}>–û—Ç–ø—Ä–∞–≤–∏—Ç—å</button>

      <p>–í—ã —É–∂–µ –ø–æ–ª—É—á–∏–ª–∏ {messages.length} –æ—Ç–≤–µ—Ç–æ–≤!</p>

      <ul>
        {messages.map((message) => (
          <li key={message.message}>{message}{" "}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
