import { useEffect, useState } from "react";
import { Helmet } from 'react-helmet';
import './App.css';

function ItemList() {
const [issues, setIssues] = useState([]);

useEffect(() => {
  fetch("http://localhost:8080/tasks")
  .then(resp => resp.json())
  .then(data => {
    setIssues(data.resp);
  })
}, [])

  return (
    <>
      <Helmet>
        <title>ToDo</title>
      </Helmet>
      <body>
      <main>
        <div>
          <center><h2>Issues</h2></center>
          <ul class="issues">
            {issues.map((user, index) => (
              <center><li key={index} className="issue-card">
                <p>Summary: {user.Summary}</p>
                <p>Description: {user.Description}</p>
              </li></center>
            ))}
          </ul>
        </div>
      </main>
      </body>
    </>
  );
}

export default ItemList;
