import { useState } from "react";

function App() {
  const [name, setName] = useState(""); // создаём состояние

  const handleChange = (event) => {
    setName(event.target.value); // обновляем имя при вводе
  };

  return (
    <div>
      <h1>Привет, {name || "гость"}!</h1>

      <input
        type="text"
        placeholder="Введите своё имя"
        value={name}
        onChange={handleChange}
      />

      <p>Ваше имя содержит {name.length} символов!</p>

      <p>Добро пожаловать в мир React!</p>
    </div>
  );
}

export default App;
