import { useState } from 'react';
import '../styles/form.css'

function IssueForm() {

    const [summary, setSummary] = useState('');
    const [description, setDescription] = useState('');
    const [status, setStatus] = useState('');

    const handleCreate = async (e) => {
        e.preventDefault();

        const data = {
            summary: summary,
            description: description,
            status: status,
        };

        try {
            const responce = await fetch('http://localhost:8080/tasks', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(data),
            });
            const result = await responce.json();
            console.log('Responce from server:', result)
        } catch (error) {
            console.error("Error while post", error)
        }
        window.location.reload()
    };

    return (
        <center><form onSubmit={handleCreate} className='form'>
            <div className='input-label'>
            <label>
                Summary
                <input type="text" name="summary" className="label-input" onChange={e => setSummary(e.target.value)}/>
            </label>
            </div>

            <div className='input-label'>
            <label>
                Description
                <input type="text" name="description" className="label-input" onChange={e => setDescription(e.target.value)}/>
            </label>
            </div>

            <div className='input-label'>
            <label>
                Status
                {['To Do', 'In Porgress', 'Done'].map(status =>(
                    (<select onChange={(e) => setStatus(e.target.value)}>
                        <option>{status}</option>
                    </select>)
                ))}
                <input type="text" name="status" className="label-input" onChange={e => setStatus(e.target.value)}/>
            </label>
            </div>

            <div className='create-btn'>
            <button>Create!</button>
            </div>
        </form></center>
    );
}

export default IssueForm;
