import {useEffect, useRef, useState } from 'react';
import { Routes, Route, NavLink, useLocation } from 'react-router-dom';

import Home from './Home';      
import ItemList from './Issues';
import CreateForm from './CreateIssue';
import Modal from './modal';

import '../styles/App.css'


function SomeApp() {
  const [menuOpen, setMenuOpen] = useState(false);
  const [modalOpen, setModalOpen] = useState(false);
  const menuRef = useRef(null);
  const location = useLocation();


  const toggleMenu = () => setMenuOpen(prev => !prev);
  const closeMenu = () => setMenuOpen(false);

  useEffect(() => {
    const handleClickOutsIssueIDe = (event) => {
      if (menuOpen && menuRef.current && !menuRef.current.contains(event.target)) {
        closeMenu();
      }
    };

    document.addEventListener('mousedown', handleClickOutsIssueIDe);
    return () => {
      document.removeEventListener('mousedown', handleClickOutsIssueIDe);
    };
  }, [menuOpen]);

  return (
    <>
      <header className='menu'>
        {!menuOpen && (
          <button className="menu-button" onClick={toggleMenu}>
            â˜° Menu
          </button>
        )}
        {!menuOpen && location.pathname === '/issues' && (<button 
          className='menu-button'
          onClick={() => setModalOpen(true)}
        >
          Create an Issue
        </button>)}
      </header>

      <nav ref={menuRef} className={`sIssueIDe-menu ${menuOpen ? 'open' : ''}`}>
        <NavLink to="/" end className={({ isActive }) => (isActive ? 'active' : '')} onClick={closeMenu}>
          Main
        </NavLink>
        <NavLink to="/issues" className={({ isActive }) => (isActive ? 'active' : '')} onClick={closeMenu}>
          Issues
        </NavLink>
      </nav>

      <main className={menuOpen ? 'blurred' : ''}>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/issues" element={<ItemList />} />
        </Routes>
      </main>

      <Modal isOpen={modalOpen} onClose={() => setModalOpen(false)}>
        <center><h2 className='create'>Create an Issue</h2></center>
        {<CreateForm/>}
      </Modal>
      </>
  );
}

export default SomeApp;
