import { useEffect, useState } from "react";
import { Helmet } from 'react-helmet';
import axios from 'axios';

import './Issues.css';

function ItemList() {
const [issues, setIssues] = useState([]);

useEffect(() => {
  axios.get("http://localhost:8080/tasks")
  .then(resp => {
    setIssues(resp.data.resp);
  })
}, [])

const deleteIssue = (id) => {
  axios.delete(`http://localhost:8080/tasks/${id}`)
  .then(() => {
    setIssues(prevIssue => prevIssue.filter(item => item.ID !== id));
  })
  .catch(error => {
    console.error('Ошибка при удалении:', error)
  });
};

  return (
    <>
      <Helmet>
        <title>ToDo</title>
      </Helmet>
      <main>
        <div>
          <center><h2>Issues</h2></center>
          <ul class="issues">
            {issues.map((user, index) => (
              <li key={index} className="issue-card">
                <p>Summary: {user.Summary}</p>
                <p>Description: {user.Description}</p>
                <p className="issue-id">ID: {user.ID}</p>
                <button className="delete-issue" onClick={() => deleteIssue(item.ID)}>❌</button>
              </li>
            ))}
          </ul>
        </div>
      </main>
    </>
  );
}

export default ItemList;
