import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, NavLink, useLocation } from 'react-router-dom';

import Home from './pages/Home';      
import ItemList from './pages/Issues';
import IssueForm from './pages/form';

import './styles/App.css';

function Modal({ isOpen, onClose, children }) {
  if (!isOpen) return null;

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-window" onClick={e => e.stopPropagation()}>
        <button className="modal-close-btn" onClick={onClose}>×</button>
        {children}
      </div>
    </div>
  );
}


function App() {
  const [menuOpen, setMenuOpen] = useState(false);
  const [modalOpen, setModalOpen] = useState(false);
  const location = useLocation();

  const toggleMenu = () => setMenuOpen(prev => !prev);
  const closeMenu = () => setMenuOpen(false);

  return (
    <Router>
      <header className='menu'>
        {!menuOpen && (
          <button className="menu-button" onClick={toggleMenu}>
            ☰ Menu
          </button>
        )}
        {!menuOpen && location.pathname === "/issues" && (<button 
          className='menu-button'
          onClick={() => setModalOpen(true)}
        >
          Create an Issue
        </button>)}
      </header>

      <nav className={`side-menu ${menuOpen ? 'open' : ''}`}>
        <NavLink to="/" end className={({ isActive }) => (isActive ? 'active' : '')} onClick={closeMenu}>
          Main
        </NavLink>
        <NavLink to="/issues" className={({ isActive }) => (isActive ? 'active' : '')} onClick={closeMenu}>
          Issues
        </NavLink>
      </nav>

      <main className={menuOpen ? 'blurred' : ''}>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/issues" element={<ItemList />} />
        </Routes>
      </main>

      <Modal isOpen={modalOpen} onClose={() => setModalOpen(false)}>
        <center><h2 className='create'>Create an Issue</h2></center>
        {<IssueForm/>}
      </Modal>
    </Router>
  );
}

export default App;
