import { useEffect, useState } from "react";

const [issues, setIssues] = useState([]);

useEffect (() => {
  fetch("http://localhost:8080/tasks")
  .then(resp => resp.json())
  .then(data => {
    setIssues(data);
  })
  .catch(error => {
    console.error("Ошибка при получении данных:", error);
  });
}, []);
