import { useState } from 'react';
import '../styles/createIssue.css'

function ChangeForm({ id }) {
  const [summary, setSummary] = useState('');
  const [description, setDescription] = useState('');
  const [status, setStatus] = useState('To Do');

  const statuses = ['To Do', 'In Progress', 'Done'];

  const handleChange = async (e) => {
    e.preventDefault();

    const data = { summary, description, status };

    try {
      const response = await fetch(`http://localhost:8080/task/${id}`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(data),
      });

      const result = await response.json();
      console.log('Response from server:', result);
    } catch (error) {
      console.error("Error while post", error);
    }

    window.location.reload();
  };

  return (
    <center>
      <form onSubmit={handleChange} className='form'>
        <div className='input-label'>
          <label>
            Summary
            <input type="text" name="summary" className="label-input" onChange={e => setSummary(e.target.value)} />
          </label>
        </div>

        <div className='input-label'>
          <label>
            Description
            <input type="text" name="description" className="label-input" onChange={e => setDescription(e.target.value)} />
          </label>
        </div>

        <div className='input-label'>
          <label>
            <div>Status</div>
            <select value={status} onChange={(e) => setStatus(e.target.value)}>
              {statuses.map((s) => (
                <option key={s} value={s}>
                  {s}
                </option>
              ))}
            </select>
          </label>
        </div>

        <div className='create-btn'>
          <button>Change!</button>
        </div>
      </form>
    </center>
  );
}


export default ChangeForm;
