import React, { useState } from 'react';
// Импортируем компоненты роутинга из react-router-dom
import { BrowserRouter as Router, Routes, Route, NavLink } from 'react-router-dom';

import Home from './Home';      
import ItemList from './Issues';

import './App.css';

function App() {
  const [menuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => setMenuOpen(prev => !prev);

  const closeMenu = () => setMenuOpen(false);

  return (
    <Router>
      <header>
        {!menuOpen && (
          <button className="menu-button" onClick={toggleMenu}>
            ☰ Menu
          </button>
        )}
      </header>

      <nav className={`side-menu ${menuOpen ? 'open' : ''}`}>
        <NavLink to="/" end className={({ isActive }) => (isActive ? 'active' : '')} onClick={closeMenu}>
          Main
        </NavLink>
        <NavLink to="/issues" className={({ isActive }) => (isActive ? 'active' : '')} onClick={closeMenu}>
          Issues
        </NavLink>
      </nav>

      <main className={menuOpen ? 'blurred' : ''}>
        <Routes>
          {/* Роут для главной страницы */}
          <Route path="/" element={<Home />} />
          {/* Роут для страницы "О нас" */}
          <Route path="/issues" element={<ItemList />} />
        </Routes>
      </main>
    </Router>
  );
}

export default App;
