import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, NavLink } from 'react-router-dom';

import Home from './Home';      
import ItemList from './Issues';

import './App.css';

function Modal({ isOpen, onClose, children }) {
  if (!isOpen) return null;

  return (
    <div style={styles.overlay} onClick={onClose}>
      <div style={styles.modal} onClick={e => e.stopPropagation()}>
        <button style={styles.closeBtn} onClick={onClose}>×</button>
        {children}
      </div>
    </div>
  );
}

function App() {
  const [menuOpen, setMenuOpen] = useState(false);
  const [modalOpen, setModalOpen] = useState(false);

  const toggleMenu = () => setMenuOpen(prev => !prev);
  const closeMenu = () => setMenuOpen(false);

  return (
    <Router>
      <header>
        {!menuOpen && (
          <button className="menu-button" onClick={toggleMenu}>
            ☰ Menu
          </button>
        )}
        <button 
          style={{ marginLeft: 10 }} 
          onClick={() => setModalOpen(true)}
        >
          Открыть модалку
        </button>
      </header>

      <nav className={`side-menu ${menuOpen ? 'open' : ''}`}>
        <NavLink to="/" end className={({ isActive }) => (isActive ? 'active' : '')} onClick={closeMenu}>
          Main
        </NavLink>
        <NavLink to="/issues" className={({ isActive }) => (isActive ? 'active' : '')} onClick={closeMenu}>
          Issues
        </NavLink>
      </nav>

      <main className={menuOpen ? 'blurred' : ''}>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/issues" element={<ItemList />} />
        </Routes>
      </main>

      <Modal isOpen={modalOpen} onClose={() => setModalOpen(false)}>
        <h2>Привет из модального окна!</h2>
        <p>Можно сюда вставлять любой контент.</p>
      </Modal>
    </Router>
  );
}

export default App;

const styles = {
  overlay: {
    position: 'fixed',
    top: 0, left: 0, right: 0, bottom: 0,
    backgroundColor: 'rgba(0,0,0,0.5)',
    display: 'flex', justifyContent: 'center', alignItems: 'center',
    zIndex: 1000,
  },
  modal: {
    backgroundColor: '#fff',
    padding: 20,
    borderRadius: 8,
    minWidth: 300,
    position: 'relative',
  },
  closeBtn: {
    position: 'absolute',
    top: 10,
    right: 10,
    fontSize: 18,
    background: 'transparent',
    border: 'none',
    cursor: 'pointer',
  },
};
