import { useState } from "react";

function App() {
  // –°–æ—Å—Ç–æ—è–Ω–∏–µ –¥–ª—è —Ö—Ä–∞–Ω–µ–Ω–∏—è –≤–≤–µ–¥—ë–Ω–Ω–æ–≥–æ –ø—É—Ç–∏ (–Ω–∞–ø—Ä–∏–º–µ—Ä, /hello?name=Ivan)
  const [path, setPath] = useState("");
  // –°–æ—Å—Ç–æ—è–Ω–∏–µ –¥–ª—è —Ö—Ä–∞–Ω–µ–Ω–∏—è –ø–æ–ª—É—á–µ–Ω–Ω—ã—Ö —Å–æ–æ–±—â–µ–Ω–∏–π –æ—Ç —Å–µ—Ä–≤–µ—Ä–∞
  const [messages, setMessages] = useState([]);

  // –§—É–Ω–∫—Ü–∏—è –¥–ª—è –æ—Ç–ø—Ä–∞–≤–∫–∏ GET-–∑–∞–ø—Ä–æ—Å–∞ –Ω–∞ —Å–µ—Ä–≤–µ—Ä –∏ –ø–æ–ª—É—á–µ–Ω–∏—è —Å–æ–æ–±—â–µ–Ω–∏—è
  const fetchMessage = () => {
    fetch(`http://localhost:8080${path}`) // –û—Ç–ø—Ä–∞–≤–ª—è–µ–º –∑–∞–ø—Ä–æ—Å –Ω–∞ —Å–µ—Ä–≤–µ—Ä –ø–æ –≤–≤–µ–¥—ë–Ω–Ω–æ–º—É –ø—É—Ç–∏
      .then(resp => {
        if (!resp.ok) {
          // –ï—Å–ª–∏ —Å–µ—Ä–≤–µ—Ä –≤–µ—Ä–Ω—É–ª –æ—à–∏–±–∫—É, –≤—ã–±—Ä–∞—Å—ã–≤–∞–µ–º –∏—Å–∫–ª—é—á–µ–Ω–∏–µ
          throw new Error(`–û—à–∏–±–∫–∞ —Å–µ—Ä–≤–µ—Ä–∞: ${resp.status}`);
        }
        return resp.json(); // –ü—Ä–µ–æ–±—Ä–∞–∑—É–µ–º –æ—Ç–≤–µ—Ç –≤ JSON
      })
      .then(data => {
        if (data.message) {
          // –ï—Å–ª–∏ –≤ –æ—Ç–≤–µ—Ç–µ –µ—Å—Ç—å –ø–æ–ª–µ message, –¥–æ–±–∞–≤–ª—è–µ–º –µ–≥–æ –≤ —Å–ø–∏—Å–æ–∫ —Å–æ–æ–±—â–µ–Ω–∏–π
          setMessages(prev => [...prev, data.message]);
        } else {
          // –ï—Å–ª–∏ –ø–æ–ª–µ message –æ—Ç—Å—É—Ç—Å—Ç–≤—É–µ—Ç, –≤—ã–≤–æ–¥–∏–º –ø—Ä–µ–¥—É–ø—Ä–µ–∂–¥–µ–Ω–∏–µ
          console.warn('–ù–µ—Ç –ø–æ–ª—è message –≤ –æ—Ç–≤–µ—Ç–µ:', data);
        }
      })
      .catch(error => {
        // –û–±—Ä–∞–±–æ—Ç–∫–∞ –æ—à–∏–±–æ–∫ –∑–∞–ø—Ä–æ—Å–∞
        console.error('–û—à–∏–±–∫–∞ –ø—Ä–∏ –ø–æ–ª—É—á–µ–Ω–∏–∏ –¥–∞–Ω–Ω—ã—Ö:', error);
      })
  };

  return (
    <div>
      <h1>–°–ø–∏—Å–æ–∫ –ø—Ä–∏–≤–µ—Ç—Å—Ç–≤–∏–π üëã</h1>

      {/* –ü–æ–ª–µ –≤–≤–æ–¥–∞ –¥–ª—è –ø—É—Ç–∏ –∑–∞–ø—Ä–æ—Å–∞ */}
      <input
        type="text"
        placeholder="–í–≤–µ–¥–∏—Ç–µ –∏–º—è"
        value={path}
        onChange={(e) => setPath(e.target.value)}
      />

      {/* –ö–Ω–æ–ø–∫–∞ –¥–ª—è –æ—Ç–ø—Ä–∞–≤–∫–∏ –∑–∞–ø—Ä–æ—Å–∞ */}
      <button onClick={fetchMessage}>–û—Ç–ø—Ä–∞–≤–∏—Ç—å</button>

      {/* –û—Ç–æ–±—Ä–∞–∂–µ–Ω–∏–µ –∫–æ–ª–∏—á–µ—Å—Ç–≤–∞ –ø–æ–ª—É—á–µ–Ω–Ω—ã—Ö —Å–æ–æ–±—â–µ–Ω–∏–π */}
      <p>–í—ã —É–∂–µ –ø–æ–ª—É—á–∏–ª–∏ {messages.length} –æ—Ç–≤–µ—Ç–æ–≤!</p>

      {/* –°–ø–∏—Å–æ–∫ –ø–æ–ª—É—á–µ–Ω–Ω—ã—Ö —Å–æ–æ–±—â–µ–Ω–∏–π */}
      <ul>
        {messages.map((message) => (
          // –ò—Å–ø–æ–ª—å–∑—É–µ–º message –≤ –∫–∞—á–µ—Å—Ç–≤–µ –∫–ª—é—á–∞ (–µ—Å–ª–∏ —Å–æ–æ–±—â–µ–Ω–∏—è —É–Ω–∏–∫–∞–ª—å–Ω—ã)
          <li key={message.message}>{message}{" "}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
